import React, { useReducer, useEffect } from "react";

// import Header from "./components/Header";
import Movie from "./components/Movie";
import spinner from "./assets/spinner.gif";
import Search from "./components/Search";
import { initialState, reducer } from "./store/reducer/index";
import axios from "axios";

// const Conference_API = "https://www.omdbapi.com/?s=man&apikey=4a3b711b";
const Conference_API = "https://o136z8hk40.execute-api.us-east-1.amazonaws.com/dev/get-list-of-conferences";

const App = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    axios.get(Conference_API).then(jsonResponse => {
      dispatch({
        type: "SEARCH_CONFERENCE_SUCCESS",
        payload: jsonResponse.data.Search
      });
    });
  }, []);

  // you can add this to the onClick listener of the Header component
  const refreshPage = () => {
    window.location.reload();
  };

  const search = searchValue => {
    dispatch({
      type: "SEARCH_CONFERENCE_REQUEST"
    });

    // axios(`https://www.omdbapi.com/?s=${searchValue}&apikey=4a3b711b`).then(
    axios(`https://o136z8hk40.execute-api.us-east-1.amazonaws.com/dev/get-list-of-conferences`).then(
      jsonResponse => {
        if (jsonResponse.data.Response === "True") {
          dispatch({
            type: "SEARCH_CONFERENCE_SUCCESS",
            payload: jsonResponse.data.Search
          });
        } else {
          dispatch({
            type: "SEARCH_CONFERENCE_FAILURE",
            error: jsonResponse.data.Error
          });
        }
      }
    );
  };

  const { conferences, errorMessage, loading } = state;

  const retrievedConferences =
    loading && !errorMessage ? (
      <img className="spinner" src={spinner} alt="Loading spinner" />
    ) : errorMessage ? (
      <div className="errorMessage">{errorMessage}</div>
    ) : (
          conferences.map((Paid, index) => (
            <Movie key={`${index}-${Paid.Title}`} Paid={Paid} />
          ))
        );

  return (
    <div className="App">
      <div className="m-container">
        {/* <Header text="Conference Hub" /> */}
        <h1>Conference Hub</h1>
        <Search search={search} />
        <p className="App-intro">Conference for the Devs By the Devs</p>
        <div className="conferences">{retrievedConferences}</div>
      </div>
    </div>
  );
};

export default App;
