import React, { useEffect, useState } from "react";
import Conference from "./components/Conference";
import spinner from "./assets/spinner.gif";
import Search from "./components/Search";
import axios from "axios";

const CONFERENCE_API_URL = "https://o136z8hk40.execute-api.us-east-1.amazonaws.com/dev/get-list-of-conferences/?s=conf&s=aws&s=Developer&s=Conference";

const App = () => {
  const [list, setList] = useState([]);
  const [filterlist, setFilterList] = useState([]);
  useEffect(() => {
    axios.get(CONFERENCE_API_URL).then(
      res => { setList(res.data.paid); setFilterList(res.data.free) }

    )
  }, []);
  // <img className="spinner" src={spinner} alt="Loading spinner" />

  return (
    <div >
      <div>
        <h1>Conference Hub</h1>
        <Search list={list} setFilterList={setFilterList} />
        <p className="App-intro">Conference list</p>
        <button style={{ width: '100px', height: '50px' }}>Free</button>
        <button style={{ width: '10opx', height: '50px' }}>Paid</button>
        <div className="conferences" style={{ display: 'flex', flexDirection: 'row' }}>{filterlist.map((conference, index) => (
          <Conference key={`${index}-${conference.confName}`} conference={conference} />
        ))}</div>
      </div>
    </div>
  );
};

export default App;
