import React from "react";

const DEFAULT_PLACEHOLDER_IMAGE =
    "https://firebasestorage.googleapis.com/v0/b/konfhub.appspot.com/o/'%2Fconference-photos%2F'Jcloud%20800x350.jpg?alt=media&token=f3966efa-8b7d-4c3d-8650-14f77ff66923";

const Movie = ({ movie }) => {
    const poster =
        movie.Poster === "N/A" ? DEFAULT_PLACEHOLDER_IMAGE : movie.Poster;
    return (
        <div className="Conference">
            <h2>{Conference.Title}</h2>
            <div>
                <img
                    width="200"
                    alt={`The movie titled: ${Conference.Title}`}
                    src={poster}
                />
            </div>
            <p>({Conference.Year})</p>
        </div>
    );
};

export default Movie;
