import React, { useReducer, useEffect, useState } from "react";

import Conference from "./components/Conference";
import spinner from "./assets/spinner.gif";
import Search from "./components/Search";
import { initialState, reducer } from "./store/reducer";
import axios from "axios";

const CONFERENCE_API_URL = "https://o136z8hk40.execute-api.us-east-1.amazonaws.com/dev/get-list-of-conferences/?s=conf&s=aws&s=Developer&s=Conference";

const App = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  let free;
  let paid;
  // const [freepayload, setFreepayload] = useState();
  // const [paidpayload, setPaidpayload] = useState();


  useEffect(() => {
    axios.get(CONFERENCE_API_URL).then(jsonResponse => {
      dispatch({
        type: "SEARCH_CONFERENCES_SUCCESS",
        payload: jsonResponse.data.paid,
      });
      free = (jsonResponse.data.free);
      paid = (jsonResponse.data.paid);
      console.log('Free', free);
      console.log('Paid', paid);

    });
  }, []);

  // you can add this to the onClick listener of the Header component
  const refreshPage = () => {
    window.location.reload();
  };

  const search = searchValue => {
    dispatch({
      type: "SEARCH_CONFERENCES_REQUEST"
    });

    axios(`https://o136z8hk40.execute-api.us-east-1.amazonaws.com/dev/get-list-of-conferences/?`).then(
      jsonResponse => {
        if (jsonResponse.data.confName === "") {
          dispatch({
            type: "SEARCH_CONFERENCES_SUCCESS",
            payload: jsonResponse.data.paid
          });
        } else {
          dispatch({
            type: "SEARCH_CONFERENCES_FAILURE",
            error: jsonResponse.data.Error
          });
        }
      }
    );
  };

  const { newconferences, conferences, loading } = state;

  const retrievedConferences =
    loading ? (
      <img className="spinner" src={spinner} alt="Loading spinner" />
    ) : (
        conferences.map((conference, index) => (
          <Conference key={`${index}-${conference.confName}`} conference={conference} />
        ))
      );

  return (
    <div className="App">
      <div className="m-container">
        <h1>Conference Hub</h1>
        <Search search={search} />
        <p className="App-intro">Conference list</p>
        <button style={{ width: '25%', height: '25%' }}>Free</button>
        <button style={{ width: '25%', height: '25%' }}>Paid</button>
        <div className="conferences" style={{ display: 'flex', flexDirection: 'row' }}>{retrievedConferences}</div>
      </div>
    </div>
  );
};

export default App;
