import React, { useState } from "react";

const Search = ({ search }) => {
    const [searchtext, setSearchText] = useState('Aman');

    const handleSearch = () => {
        setSearchText(e.target.value)
    }
    return (
        <form className="search">
            <input
                value={searchtext}
                type="text"
                onChange={handleSearch}
            />
            <input type="submit" value="SEARCH" />
        </form>
    );
};

export default Search;
